import React, { useState, useEffect } from 'react';

function App() {
  const [catImage, setCatImage] = useState('');
  const [catFact, setCatFact] = useState('');

  useEffect(() => {
    // Funci贸n para obtener una imagen aleatoria de gatos
    fetch('https://api.thecatapi.com/v1/images/search')
      .then(response => response.json())
      .then(data => setCatImage(data[0].url))
      .catch(error => console.error('Error fetching cat image:', error));

    // Funci贸n para obtener un hecho aleatorio de gatos
    fetch('https://cat-fact.herokuapp.com/facts/random')
      .then(response => response.json())
      .then(data => setCatFact(data.text))
      .catch(error => console.error('Error fetching cat fact:', error));
  }, []);

  return (
    <div className="App">
      <CatImage imageUrl={catImage} />
      <CatFact fact={catFact} />
      <button onClick={obtenerNuevoHechoYImagen}>Obtener nuevo hecho y nueva imagen</button>
    </div>
  );
}

function CatImage({ imageUrl }) {
  return (
    <div className="cat-image">
      <img src={imageUrl} alt="Imagen de gato" />
    </div>
  );
}

function CatFact({ fact }) {
  return (
    <div className="cat-fact">
      <p>{fact}</p>
    </div>
  );
}

export default App;
